let count = landmarks.length;

landmarks.forEach((lm, i) => {
  var chars = lm.where.split("");
  var spanned = chars.map(char => `<span>${char}</span>`);
  var where = document.querySelector(".where");
  var place = document.createElement("div");
  var what = document.querySelector(".what");
  place.innerHTML = spanned.join("");
  place.classList.add("place");
  where.appendChild(place);
  var name = document.createElement("div");
  name.innerHTML = lm.name;
  name.classList.add("name");
  what.appendChild(name);

  if (i === 0) {
    for (var i = 0; i < place.children.length; i++) {
      place.children[i].style.opacity = 1;
    }
    name.style.opacity = 1;
  }
});

var index = 0;
function animate() {
  anime({
    targets: ".where div:nth-child(" + (index + 1) + ") span",
    opacity: {
      value: 0,
      duration: 10
    }
  });
  anime({
    targets: ".what div:nth-child(" + (index + 1) + ")",
    opacity: {
      value: 0,
      duration: 10
    }
  });
  index++;
  index %= count;
  anime({
    targets: ".where div:nth-child(" + (index + 1) + ") span",
    opacity: {
      value: 1,
      duration: 400,
      delay: (el, index) => index * 30 + 100
    }
  });
  anime({
    targets: ".what div:nth-child(" + (index + 1) + ")",
    opacity: {
      value: 1,
      duration: 400,
      delay: 200
    }
  });
  anime({
    targets: "path",
    d: {
      value: landmarks[index].d,
      duration: 1000
    },
    elasticity: 100
  });
}

function schedule() {
  setTimeout(() => {
    animate();
    schedule();
  }, 3000);
}

schedule();
